import path from 'path';
import constants from './constants.js';

export function getUrl(url) {
    return '//' + path.join(constants.API_BASE, url); 
}

export function getStatic(url) {
    return path.join(constants.PUBLIC_URL, url)
}

export function getAdditionalHeaders() {
    return {
        'token': constants.TOKEN
    }
}

export function checkLogin(){
    return new Promise((resolve, reject) => {
        fetch(
            getUrl('/userLogin/'),
            {
                method: 'HEAD',
                headers: {
                    ...getAdditionalHeaders()
                }
            }
        )
        .then(res => res.status === 200 ? resolve(true) : reject(false))
        .catch(error => reject(false));
    });
}

export function get(url, headers, extraConf={}){
    return fetch(
        getUrl(url),
        {
            method :'GET',
            headers: {
                ...headers,
                ...getAdditionalHeaders()
            },
            ...extraConf
        }
    )
}

export function post(url, headers, body, extraConf={}){
    return fetch(
        getUrl(url),
        {
            method: 'POST',
            headers: {
                ...headers,
                ...getAdditionalHeaders()
            },
            body: JSON.stringify(body),
            ...extraConf
        }
    )
}

export function getimages(){
    var imagetags=[];
    get(
        '/data/images/'
    )
    .then(data => data.json())
    .then(function(data)
    {
        for(var i=0;i<data.length;i++){
            imagetags.push(
            <div className="card" key={i}>
               <div className="media">
                    <div className="media-left">
                        <figure className="image is-48x48">
                        <img 
                        src={data[i]["person_uploaded"]["Image"][0] === '/' ? getStatic(data[i]["person_uploaded"]["Image"]) : data[i]["person_uploaded"]["Image"]}
                        alt="profile" key={i+5}/>
                        </figure>
                    </div>
                    <div className="media-content">
                        <p className="title is-4">{data[i]["person_uploaded"]["username"]}</p>
                    </div>
                </div>
                <div className="card-image">
                    <figure className="image is-5by4">
                        <img src={data[i]["Image"][0] === '/' ? getStatic(data[i]["Image"]) : data[i]["Image"]}
                        key={i}
                        alt="give the proper url"/>
                    </figure> 
                </div>
                <div className="like">
                    <i className="far fa-heart"></i>
                </div>
                <div className="like">
                    <i className="far fa-comment"></i>
                </div>
                <div className="like">
                    <i className="fas fa-share"></i>
                </div>
                <div className="card-content">
                        <a href="/profiledata"><b className="pstyle">{data[i]["person_uploaded"]["username"]}</b></a>
                        <p className="pstyle">&nbsp;{data[i]["description"]}</p>
                </div>
            </div>

            );
        }
    })
    .catch(console.log)
    return imagetags;
}
