import React from 'react';
import {getStatic, checkLogin, get} from './utilities';
import {Redirect ,Link} from 'react-router-dom';
var imagetags=[];
export default class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {offset: 0, count: 10};
    }

    async getimages(){
        await get(
            '/data/images/'
        )
        .then(data => data.json())
        .then(function(data)
        {
            console.log(data);
            JSON.stringify(data);
            console.log(data)
            for(var i=0;i<data.length;i++){
                console.log(data[i]["Image"]);
                imagetags.push(<img src={data[i][Image]}
                key={i}
                alt="give the proper url"/>);
            }
            console.log(imagetags);
        })
        .catch(console.log)
        return (
            <div>
                {imagetags};
            </div>);
    }
    async UNSAFE_componentWillMount() {
        this.setState({
            isLoggedIn: await checkLogin(),
            offset: this.state.offset + this.state.count,
            count: this.state.count
        });

    }


getprofile() {
    //   get(
    //       '/data/user/get',
    //        {'Content-Type':'application/json'}
    //     )
    //     .then(data => data.json())
    //     .then(console.log)
    //     .catch(console.log)
    console.log("sdg");
        return <Redirect to="/profile" />
  }

async getinputdata(event){
    if(event.keyCode === 13){
        const name= document.getElementById("input").value;
        await get(
            '/data/user/?username='+name,
            {'Content-Type' : 'application/json'}
        )
        .then(data=>data.json())
        .then(console.log)
        .catch(console.log)
    }
}
    render() {
        if(this.state['isLoggedIn']){
            return  (
        <div className="container">
            <div className="setitems">
                <div className="inl">
                    <img className="imgset" src={getStatic("/index.png")}
                            alt="Placeholder" 
                    />
                </div>
                <div className="fontsize">
                    <span>
                        Instagram
                    </span>
                </div>
                <div className="inl">
                    <input 
                        className="input is-warning is-rounded" 
                        type="text" 
                        onKeyDown={this.getinputdata} 
                        placeholder="search" 
                        id="input"
                    />
                </div>
                <div className="inl">
                    <Link to="/profile">
                        <img 
                            className="imgset" src={getStatic("/images.png")} 
                            alt="Placeholder" />
                    </Link>
                </div>
            </div>
            <div className="positionimgs">
                <div className="cardset">
                    <div className="card-image">
                        <figure className="image is-5by2">
                            <img 
                                className="imgset" src={getStatic("/images.png")} 
                                alt="Placeholder"
                            /> 
                        </figure>
                    </div>
                </div>
                <div className="container">
                    <this.getimages/>
                </div>
                <div className="cardset">
                    <div className="card-image">
                        <figure className="image is-5by2">
                            <img 
                                 src={getStatic("/images.png")}
                                 alt="Placeholder" 
                            /> 
                        </figure>
                    </div>
                    <div className="card-content">
                        <h1>
                            this is the profile image
                        </h1>
                    </div>
                </div>
                <div className="cardset">
                    <div className="card-image">
                        <figure className="image is-5by2">
                            <img 
                                src={getStatic("/images.png")}
                                alt="Placeholder" 
                            /> 
                        </figure>
                    </div>
                </div>
            </div>
        </div>
            );    
        }   else    {
            // Redirect To Login
            return (<div>You are not logged in</div>);
        }

    } 
}
