import React, {Component} from 'react';
import axios from 'axios';

export default class LoginForm extends React.Component{
    constructor(props){
        super(props);
        this.state={
            loggedin : false,
        };
    }
 loginform =()=>(
        <div id="login"> 
    <label >Username:</label>
    <input type="text" name="username" />
    <label>Password:</label>
    <input type="password" name="password" />
    <button onClick={this.getdata} type="submit" value="Login">login</button>
    </div>
    );

    logoutform = ()=>(
        <div>
            <button type="submit">logout</button>
        </div>
    );
getdata(){
        var self =this;
        var username=document.getElementsByName("username")[0].value;
        var password=document.getElementsByName("password")[0].value;
        axios.post('http://127.0.0.1:8000/login/',{headers: {'Content-Type': 'application/json'}},{data:{username,password}})
            .then(res=>{
                this.setState({loggedin :true});
            })
            .catch(error=>{
                console.log(error)
            });
    }

    render(){
        return(
            <div>
                {this.state.loggedin ? <this.logoutform/> : <this.loginform/>}
            </div>
        );
    }
}
