import React from 'react';
import {getStatic, checkLogin, get} from './utilities';
import {Redirect ,Link} from 'react-router-dom';
var imagetags=[];
export default class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {offset: 0, count: 10};
    }

    async getimages(){

    }

    async UNSAFE_componentWillMount() {
        await get(
            '/data/images/'
        )
        .then(data => data.json())
        .then(function(data)
        {
            for(var i=0;i<data.length;i++){
                imagetags.push(
                <div className="card" key={i}>
                   <div className="media">
                        <div className="media-left">
                            <figure className="image is-48x48">
                            <img 
                            src={data[i]["person_uploaded"]["Image"][0] === '/' ? getStatic(data[i]["person_uploaded"]["Image"]) : data[i]["person_uploaded"]["Image"]}
                            alt="profile" key={i}/>
                            </figure>
                        </div>
                        <div className="media-content">
                            <p className="title is-4">{data[i]["person_uploaded"]["username"]}</p>
                        </div>
                    </div>
                    <div className="card-image">
                        <figure className="image is-5by4">
                            <img src={data[i]["Image"][0] === '/' ? getStatic(data[i]["Image"]) : data[i]["Image"]}
                            key={i}
                            alt="give the proper url"/>
                        </figure> 
                    </div>
                    <div className="like">
                        <i className="far fa-heart"></i>
                        <i className="far fa-comment"></i>
                        <i class="fal fa-share"></i>
                    </div>
                    <div className="card-content">
                            <p>{data[i]["description"]}</p>
                    </div>
                </div>

                );
            }
        })
        .catch(console.log)
        this.setState({
            images:imagetags,
            isLoggedIn: await checkLogin(),
            offset: this.state.offset + this.state.count,
            count: this.state.count
        });
    }


getprofile() {
