    //       '/data/user/get',
    //        {'Content-Type':'application/json'}
    //     )
    //     .then(data => data.json())
    //     .then(console.log)
    //     .catch(console.log)
    console.log("sdg");
        return <Redirect to="/profile" />
  }

async getinputdata(event){
    if(event.keyCode === 13){
        const name= document.getElementById("input").value;
        await get(
            '/data/user/?username='+name,
            {'Content-Type' : 'application/json'}
        )
        .then(data=>data.json())
        .then(console.log)
        .catch(console.log)
    }
}
    render() {
        if(this.state['isLoggedIn']){
            return  (
        <div className="container">
            <div className="setitems">
                <div className="inl">
                    <img className="imgset" src={getStatic("/index.png")}
                            alt="Placeholder" 
                    />
                </div>
                <div className="fontsize">
                    <span>
                        Instagram
                    </span>
                </div>
                <div className="inl">
                    <input 
                        className="input is-warning is-rounded" 
                        type="text" 
                        onKeyDown={this.getinputdata} 
                        placeholder="search" 
                        id="input"
                    />
                </div>
                <div className="inl">
                    <Link to="/profile">
                        <img 
                            className="imgset" src={getStatic("/images.png")} 
                            alt="Placeholder" />
                    </Link>
                </div>
            </div>
            <div className="cardset">
            {this.state.images}
            </div>
    </div>
            );    
        }   else    {
            // Redirect To Login
            return (<div>You are not logged in</div>);
        }

    } 
}
