// var path = require('path');
import path from 'path';
import constants from './constants.js';

export function getUrl(url) {
    return '//' + path.join(constants.API_BASE, url); 
}

export function getStatic(url) {
    return path.join(constants.PUBLIC_URL, url)
}

export function getAdditionalHeaders() {
    return {
        'token': 'XSRF-TOKEN: jeXqBw6hj918WEO8WKpI1kkUL68FDWgvC32rwtki2L9xgVYAF9rgxEAAab0IEUwQ; sessionid: q8qdph1qo7kt6twy5del2qd1db51qls5'
    }
}

function checkLogin(){
    fetch(
        getUrl('/userLogin/'),
        {
            method: 'GET',
        }
    )
    .then(res=>{
        console.log(res);
    })
    .catch(error=>{
        console.log(error)
    });
}
