import React, {Component} from 'react';

var kk = {
    margin : "-10px"
        }
var lk = {
    height : "100%"
}

function makeRequest(){
    var xhr = new XMLHttpRequest();

    xhr.onreadystatechange = () => {
        if(xhr.readyState == 4 && xhr.status == 200) {
            var myData = JSON.parse(xhr.responseText);
            document.getElementById("akk").innerHTML = myData.username;
            if(typeof myData == 'array') {
                for(var i = 0; i < myData.length; i++) {
                    console.log(myData[i]);
                }    
            }   else    {
                console.log(myData);
            }
        }   
    } 

    xhr.open('GET', '/user/' + document.getElementById('rara').value);
    xhr.send()
}

class Home extends React.Component {
    componentDidMount(){
        axios.get('http://127.0.0.1:8000/user/')
            .then(function (response) {console.log(response);})
    }
    render() {
        return  (<div style={lk}><center> <img src="index.png" width="30" height="30" style={kk}/> &nbsp;&nbsp;&nbsp; Instagram &nbsp; &nbsp; &nbsp;&nbsp;
        <input type="text" placeholder="search"/> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <a><img src="images.png" onClick={makeRequest} width="30" height="30" style={kk}/></a> </center>
        <input type="number" id="rara"></input>
        </div>);
    } 
}
export default Home;
