import React from 'react';
import { get, getStatic } from './utilities';

export default class profile extends React.Component{
    constructor(props){
        super(props);
        this.state={
            images: []
        };
    }
    async UNSAFE_componentDidMount(){
        await get(
            '/data/user/get',
            {'Content-Type':'application/json'}
         )
         .then(res => res.json())
         .then(data => {
             this.setState({
                images: [data["Image"]]
             });
         })
         .catch(console.log)
    }
    render(){
        return(
            //   <div>username is{typeof this.state["Image"]} </div>
            <div className="container">
                <div className="card">
                    <div className="media">
                        <div className="media-left">
                            {this.state["Image"]}
                            <figure className="image is-48x48">
                                <img 
                                    src={this.state.images.map(function (element, i) {
                                            return element;
                                        }
                                    )}
                                alt="profile"/>
                            </figure>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
