import React from 'react';
import {getStatic, checkLogin, get} from './utilities';
import {Redirect ,Link} from 'react-router-dom';
var imagetags=[];
export default class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {offset: 0, count: 10};
    }

    async getimages(){
        var self=this;
        await get(
            '/data/images/'
        )
        .then(data => data.json())
        .then((data)=>
        {
            console.log(self);
            for(var i=0;i<data.length;i++){
                console.log(data[i]["Image"]);
                imagetags.push(<img src={data[i]["Image"][0] === '/' ? getStatic(data[i]["Image"]) : data[i]["Image"]}
                key={i}
                alt="give the proper url"/>);
            }
            this.setState({
                images:imagetags,
                isLoggedIn: this.state.isLoggedIn,
                offset: this.state.offset + this.state.count,
                count: this.state.count
            });
        })
        .catch(console.log)
    }

    async UNSAFE_componentWillMount() {
        this.setState({
            isLoggedIn: await checkLogin(),
            offset: this.state.offset + this.state.count,
            count: this.state.count
        });
    }


getprofile() {
